import Link from 'next/link'

class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        active: false,
    };
  }

  toggleMenu = () => {
    console.log(this);
    this.setState({
      active: !this.state.active
    });
    // event.classList.toggle("change"); 
  }

render() {
  // const linkStyle = {
  //   marginRight: 15
  // }
  return(
    <div className="container" onClick={this.toggleMenu.bind(this)}>
        <div className={"bar bar1 " + (this.state.active ? ' change' : null)}></div>
        <div className={"bar bar2 " + (this.state.active ? ' change' : null)}></div>
        <div className={"bar bar3 " + (this.state.active ? ' change' : null)}></div>
        <div className={"topnav " + (this.state.active ? ' change' : null)} id="myTopnav">
          <a href="#home" className="active">Home</a>
          <a href="#news">News</a>
          <a href="#contact">Contact</a>
          <a href="#about">About</a>
          <a href="javascript:void(0);" className="icon" onclick="myFunction()">
            <i className="fa fa-bars"></i>
          </a>
        </div>
        <style jsx>{`
          .container {
            display: inline-block;
            cursor: pointer;
            align-self: flex-start;
          }

          .bar {
            width: 35px;
            height: 5px;
            background-color: black;
            margin: 6px 0;
          }

          .change.bar1 {
            -webkit-transform: rotate(-45deg) translate(-9px, 6px) ;
            transform: rotate(-45deg) translate(-9px, 6px) ;
          }

          .change.bar2 {
            opacity: 0;
          }

          .change.bar3 {
            -webkit-transform: rotate(45deg) translate(-8px, -8px) ;
            transform: rotate(45deg) translate(-8px, -8px) ;
          }

          .topnav {
            opacity: 0;
          }

          .topnav.change {
            opacity: 100;
            display: flex;
            flex-direction: column;
            align-items: center;
            background: gray;
            width: 100%;
          }
        `}</style>
      {/* <Link href="/">
        <a style={linkStyle}>Home</a>
      </Link>
      <Link href="/about">
        <a style={linkStyle}>About</a>
      </Link> */}
    </div>
  )};
}
export default Header
