    .attr("class", "bubble");

    return (
      <div className="index">
        {/* <Header /> */}
        <div className="profile_text">
          <h3>Traci Thompson | Full Stack Developer</h3>
        </div>
        <div className="banner" ref={this.bannerRef}>
