import Link from 'next/link'

class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        active: false,
    };
  }

  toggleMenu = () => {
    console.log(this);
    this.setState({
      active: !this.state.active
    });
    // event.classList.toggle("change"); 
  }

  /* Toggle between adding and removing the "responsive" class to topnav when the user clicks on the icon */
 myFunction = () => {
  var x = document.getElementById("myTopnav");
  if (x.className === "topnav") {
    x.className += " responsive";
  } else {
    x.className = "topnav";
  }
}

render() {
  // const linkStyle = {
  //   marginRight: 15
  // }
  return(
    <div className="container" onClick={this.toggleMenu.bind(this)}>
        <div className={"bar bar1 " + (this.state.active ? ' change' : null)}></div>
        <div className={"bar bar2 " + (this.state.active ? ' change' : null)}></div>
        <div className={"bar bar3 " + (this.state.active ? ' change' : null)}></div>
        <div className="topnav" id="myTopnav">
          <a href="#home" className="active">Home</a>
          <a href="#news">News</a>
          <a href="#contact">Contact</a>
          <a href="#about">About</a>
          <a href="javascript:void(0);" className="icon" onclick="myFunction()">
            <i className="fa fa-bars"></i>
          </a>
        </div>
        <style jsx>{`
          .container {
            display: inline-block;
            cursor: pointer;
            align-self: flex-start;
          }

          .bar {
            width: 35px;
            height: 5px;
            background-color: black;
            margin: 6px 0;
          }

          .change.bar1 {
            -webkit-transform: rotate(-45deg) translate(-9px, 6px) ;
            transform: rotate(-45deg) translate(-9px, 6px) ;
          }

          .change.bar2 {
            opacity: 0;
          }

          .change.bar3 {
            -webkit-transform: rotate(45deg) translate(-8px, -8px) ;
            transform: rotate(45deg) translate(-8px, -8px) ;
          }

          /* Add a black background color to the top navigation */
          .topnav {
            background-color: #333;
            overflow: hidden;
          }

          /* Style the links inside the navigation bar */
          .topnav a {
            float: left;
            display: block;
            color: #f2f2f2;
            text-align: center;
            padding: 14px 16px;
            text-decoration: none;
            font-size: 17px;
          }

          /* Change the color of links on hover */
          .topnav a:hover {
            background-color: #ddd;
            color: black;
          }

          /* Add an active class to highlight the current page */
          .active {
            background-color: #4CAF50;
            color: white;
          }

          /* Hide the link that should open and close the topnav on small screens */
          .topnav .icon {
            display: none;
          }

          /* When the screen is less than 600 pixels wide, hide all links, except for the first one ("Home"). Show the link that contains should open and close the topnav (.icon) */
          @media screen and (max-width: 600px) {
            .topnav a:not(:first-child) {display: none;}
            .topnav a.icon {
              float: right;
              display: block;
            }
          }

          /* The "responsive" class is added to the topnav with JavaScript when the user clicks on the icon. This class makes the topnav look good on small screens (display the links vertically instead of horizontally) */
          @media screen and (max-width: 600px) {
            .topnav.responsive {position: relative;}
            .topnav.responsive a.icon {
              position: absolute;
              right: 0;
              top: 0;
            }
            .topnav.responsive a {
              float: none;
              display: block;
              text-align: left;
            }
          }
        `}</style>
      {/* <Link href="/">
        <a style={linkStyle}>Home</a>
      </Link>
      <Link href="/about">
        <a style={linkStyle}>About</a>
      </Link> */}
    </div>
  )};
}
export default Header
